using System; namespace Serial {     public class PositionCalculator     {         public XYZ StartPosition = new XYZ();         public XYZ CurrentPosition = new XYZ();          public PositionCalculator()         {          }  		public void SetStartPoint(double x = 0, double y = 0, double z = 0)         {             StartPosition.X = x;             StartPosition.Y = y;             StartPosition.Z = z;         }          private void UpdatePosition(XYZ impact)         {             CurrentPosition.X += impact.X;             CurrentPosition.Y += impact.Y;             CurrentPosition.Z += impact.Z;         }          public void CalculatePositionFromAccelerometer(DataPoint dataPoint)         {             XYZ impact = new XYZ(); 			impact.X = dataPoint.Coordinates.X * dataPoint.TimeInSec * dataPoint.TimeInSec; 			impact.Y = dataPoint.Coordinates.Y * dataPoint.TimeInSec * dataPoint.TimeInSec; 			impact.Z = dataPoint.Coordinates.Z * dataPoint.TimeInSec * dataPoint.TimeInSec;             UpdatePosition(impact);         }          public void ResetPossition(XYZ position)         {             CurrentPosition.X = position.X;             CurrentPosition.Y = position.Y;             CurrentPosition.Z = position.Z;         }     } } 